INSERT INTO `terraform_template_data` VALUES (1, 'main', 4, 'create_server', 'bindPublicIp:false,bindPublicIp:true', 'terraform {\n	required_providers {\n		alicloud = {\n			source = \"aliyun/alicloud\"\n			version = \"1.189.0\"\n		}\n	}\n}\n\nprovider \"alicloud\" {\n	access_key=var.input_secret_ak\n	secret_key=var.input_secret_sk\n	region=var.input_region_cid\n}\n\nvariable \"pre_paid_period\" {\n	default=1\n}\n\nresource \"alicloud_instance\" \"instance\" {\n	availability_zone       = var.input_zone_cid\n	image_id                = var.input_server_image_cid\n	data_disks  {\n		size                = var.input_disk_size\n		name            = var.input_name\n		category            = var.input_disk_type_cid\n		performance_level   = var.input_performance_level == \"normal\"? null : var.input_performance_level\n	}\n	tags = {\n		project = var.input_cloud_project_name\n	}\n	instance_name               = var.input_name\n	host_name                   = var.input_name\n	instance_charge_type        = var.input_charge_type_cid\n	period                      = var.input_charge_type_cid == \"PrePaid\" ? var.pre_paid_period : null\n	renewal_status              = var.input_charge_type_cid == \"PrePaid\" ? var.input_renewal_type_cid : null\n	auto_renew_period           = var.input_charge_type_cid == \"PrePaid\" ? var.pre_paid_period : null\n	include_data_disks          = true\n	system_disk_category        = var.input_disk_type_cid\n	system_disk_size            = \"52\"\n	system_disk_name    = var.input_name\n	instance_type               = var.input_server_spec_cid\n	#password                   = var.remainder_loginPassword\n	key_name                    = var.input_key_pair_name\n	#internet_max_bandwidth_out  = var.remainder_bandwidth\n	#internet_charge_type        = var.remainder_bindPublicIp == \"true\" ? \"PayByTraffic\" : null\n	resource_group_id           = var.input_cloud_project_cid\n	security_groups             = [var.input_security_group_cid]\n	vswitch_id                  = var.input_subnet_cid\n}\n\nresource \"alicloud_eip_address\" \"eip_instance\" {\n	count                 = var.input_bind_public_ip == \"true\" ? 1 : 0\n	address_name          = \"${alicloud_instance.instance.instance_name}-eip\"\n	bandwidth             = var.input_bandwidth\n	internet_charge_type  = \"PayByTraffic\"\n	isp                   = \"BGP\"\n	netmode               = \"public\"\n	payment_type          = \"PayAsYouGo\"\n	resource_group_id     = var.input_cloud_project_cid\n	tags = {\n		project = var.input_cloud_project_name\n	}\n}\nresource \"alicloud_eip_association\" \"associated\" {\n	count                 = var.input_bind_public_ip == \"true\" ? 1 : 0\n	allocation_id   = alicloud_eip_address.eip_instance[0].id\n	instance_id     = alicloud_instance.instance.id\n}\n');
INSERT INTO `terraform_template_data` VALUES (2, 'output', 4, 'create_server', 'bindPublicIp:false', 'output \"resource_id\" {\n  value = alicloud_instance.instance.id\n}\noutput \"resource_name\" {\n  value = alicloud_instance.instance.host_name\n}\noutput \"resource_ip_addr\" {\n  value = alicloud_instance.instance.private_ip\n}');
INSERT INTO `terraform_template_data` VALUES (3, 'output', 4, 'create_server', 'bindPublicIp:true', 'output \"resource_id\" {\n  value = alicloud_instance.instance.id\n}\n\noutput \"resource_name\" {\n  value = alicloud_instance.instance.host_name\n}\n\noutput \"resource_ip_addr\" {\n  value = alicloud_instance.instance.private_ip\n}\n\noutput \"public_resource_ip_addr\" {\n  value = join(\"\", alicloud_eip_address.eip_instance[*].ip_address)\n}');
INSERT INTO `terraform_template_data` VALUES (4, 'main', 1, 'create_server', 'bindPublicIp:false,bindPublicIp:true', 'terraform {\n	required_providers {\n		baiducloud = {\n			source  = \"baidubce/baiducloud\"\n			version = \"1.19.35\"\n		}\n	}\n}\n\nprovider \"baiducloud\" {\n	access_key = var.input_secret_ak\n	secret_key = var.input_secret_sk\n	region     = trimprefix(var.input_region_cid,\"cn-\")\n}\ndata \"baiducloud_bcc_key_pairs\" \"name\" {\n	name = var.input_key_pair_name\n}\nresource \"baiducloud_instance\" \"instance\" {\n	availability_zone = var.input_zone_cid\n	image_id          = var.input_server_image_cid\n	cds_disks {\n		cds_size_in_gb = var.input_disk_size\n		storage_type   = var.input_disk_type_cid\n	}\n	reservation = var.input_charge_type_cid == \"Prepaid\" ? {\n		reservation_length    = \"1\"\n		reservation_time_unit = \"month\"\n	} : null\n	tags = {\n		project = var.input_cloud_project_name\n	}\n	hostname               = var.input_name\n	auto_renew_time_length = \"1\"\n	auto_renew_time_unit   = var.input_charge_type_cid == \"Prepaid\" && var.input_renewal_type_cid == \"auto\" ? \"month\" : null\n	cds_auto_renew         = var.input_charge_type_cid == \"Prepaid\" ? \"true\" : \"false\"\n	payment_timing         = var.input_charge_type_cid\n	name                   = var.input_name\n	instance_spec          = var.input_server_spec_cid\n	keypair_id             = \"${data.baiducloud_bcc_key_pairs.name.key_pairs.0.keypair_id}\"\n	security_groups        = [var.input_security_group_cid]\n	subnet_id              = var.input_subnet_cid\n	related_release_flag = \"true\"\n	resource_group_id  = var.input_cloud_project_cid\n	root_disk_storage_type =  var.input_disk_type_cid\n	root_disk_size_in_gb = \"50\"\n	relation_tag         = \"true\"\n}\n\nresource \"baiducloud_eip\" \"default\" {\n	count             = var.input_bind_public_ip == \"true\" ? 1 : 0\n	name              = \"${baiducloud_instance.instance.name}-eip\"\n	bandwidth_in_mbps = var.input_bandwidth\n	payment_timing    = \"Postpaid\"\n	billing_method    = \"ByTraffic\"\n	tags = {\n		project = var.input_cloud_project_name\n	}\n}\n\nresource \"baiducloud_eip_association\" \"instance\" {\n	count         = var.input_bind_public_ip == \"true\" ? 1 : 0\n	eip           = baiducloud_eip.default[0].eip\n	instance_type = \"BCC\"\n	instance_id   = baiducloud_instance.instance.id\n}');
