INSERT INTO `terraform_template_data` VALUES (6, 'main', 3, 'create_server', 'bindPublicIp:false,bindPublicIp:true', 'terraform {\n	required_providers {\n		tencentcloud = {\n			source = \"tencentcloudstack/tencentcloud\"\n			version = \"1.81.16\"\n		}\n	}\n\n}\n\nprovider \"tencentcloud\" {\n	secret_id=var.input_secret_ak\n	secret_key=var.input_secret_sk\n	region=var.input_region_cid\n}\n\nvariable \"prePaidPeriod\" {\n	default=1\n}\ndata \"tencentcloud_key_pairs\" \"name\" {\n	key_name = var.input_key_pair_name\n}\nresource \"tencentcloud_instance\" \"instance\" {\n	availability_zone                       = var.input_zone_cid\n	image_id                                = var.input_server_image_cid\n	data_disks  {\n		data_disk_size = var.input_disk_size\n		data_disk_type = var.input_disk_type_cid\n		delete_with_instance = true\n	}\n	// 腾讯云provider默认不能使用变量作为key，这里写死key值\n	tags = {\n		project = var.input_cloud_project_name\n	}\n	hostname                                = var.input_name\n	instance_charge_type_prepaid_period     = var.input_charge_type_cid == \"PREPAID\" ? var.prePaidPeriod : null\n	instance_charge_type_prepaid_renew_flag = var.input_charge_type_cid == \"PREPAID\" ? var.input_renewal_type_cid : null\n	instance_charge_type                    = var.input_charge_type_cid\n	#internet_max_bandwidth_out             = var.remainder_bindPublicIp == \"true\" ? var.remainder_bandwidth : null\n	instance_name                           = var.input_name\n	instance_type                           = var.input_server_spec_cid\n	project_id                              = var.input_cloud_project_cid\n	#password                               = var.remainder_loginPassword\n	key_name                                    = \"${data.tencentcloud_key_pairs.name.key_pair_list.0.key_id}\"\n	security_groups                         = [var.input_security_group_cid]\n	stopped_mode                            = \"STOP_CHARGING\"\n	subnet_id                               = var.input_subnet_cid\n	system_disk_type                        = var.input_disk_type_cid\n	vpc_id                                  = var.input_vpc_cid\n}\nresource \"tencentcloud_eip\" \"eip_instance\" {\n	count                         = var.input_bind_public_ip == \"true\" ? 1 : 0\n	name                          = \"${tencentcloud_instance.instance.instance_name}-eip\"\n	internet_max_bandwidth_out    = var.input_bandwidth\n	internet_charge_type          = \"TRAFFIC_POSTPAID_BY_HOUR\"\n	internet_service_provider     = \"BGP\"\n	#resource_group_id            = var.input_cloud_project_cid\n	tags = {\n		project              = var.input_cloud_project_name\n	}\n}\nresource \"tencentcloud_eip_association\" \"associated\" {\n	count                 = var.input_bind_public_ip == \"true\" ? 1 : 0\n	eip_id                = tencentcloud_eip.eip_instance[0].id\n	instance_id           = tencentcloud_instance.instance.id\n}');
