INSERT INTO `terraform_template_data` VALUES (8, 'output', 3, 'create_server', 'bindPublicIp:true', 'output \"resource_id\" {\n  value = tencentcloud_instance.instance.id\n}\n\noutput \"resource_name\" {\n  value = tencentcloud_instance.instance.hostname\n}\n\noutput \"resource_ip_addr\" {\n  value = tencentcloud_instance.instance.private_ip\n}\n\noutput \"public_resource_ip_addr\" {\n  value = join(\"\", tencentcloud_eip.eip_instance[*].public_ip)\n}');
INSERT INTO `terraform_template_data` VALUES (9, 'main', 2, 'create_server', 'bindPublicIp:true', 'terraform {\n	required_providers {\n		huaweicloud = {\n			source = \"huaweicloud/huaweicloud\"\n			version = \"1.44.2\"\n		}\n	}\n}\n\nprovider \"huaweicloud\" {\n	region=var.input_region_cid\n	access_key=var.input_secret_ak\n	secret_key=var.input_secret_sk\n}\n\nresource \"huaweicloud_compute_instance\" \"instance\" {\n	region                      = var.input_region_cid\n	name                        = var.input_name\n	image_id                    = var.input_server_image_cid\n	security_group_ids          = [var.input_security_group_cid]\n	availability_zone           = var.input_zone_cid\n	#admin_pass                  = var.remainder_loginPassword\n	key_pair                = var.input_key_pair_name\n	user_id    = \"fde318a43e6b4bbca7b201782dea8515\"\n	flavor_id                   = var.input_server_spec_cid\n	data_disks {\n		type = var.input_disk_type_cid\n		size = var.input_disk_size\n	}\n	// provider默认不能使用变量作为key，这里写死key值\n	tags = {\n		project = var.input_cloud_project_name\n	}\n	enterprise_project_id       = var.input_cloud_project_cid\n	delete_disks_on_termination = true\n	agent_list  = \"ces\"\n	charging_mode               = var.input_charge_type_cid\n	period_unit                 = var.input_charge_type_cid == \"prePaid\" ? \"month\" : null\n	period                      = var.input_charge_type_cid == \"prePaid\" ? 1 : null\n	auto_renew                  = var.input_charge_type_cid == \"prePaid\" ? true : null\n	network {\n		uuid = var.input_subnet_cid\n	}\n}\n\nresource \"huaweicloud_vpc_eip\" \"eip\" {\n	publicip {\n		type = \"5_bgp\"\n	}\n\n	bandwidth {\n		share_type  = \"PER\"\n		name        = \"bwpkg_${var.input_name}\"\n		size        = var.input_bandwidth\n		charge_mode = \"traffic\"\n	}\n\n	tags = {\n		project = var.input_charge_type_cid\n	}\n	enterprise_project_id   = var.input_cloud_project_cid\n	period_unit             = var.input_charge_type_cid == \"prePaid\" ? \"month\" : null\n	period                  = var.input_charge_type_cid == \"prePaid\" ? 1 : null\n	auto_renew              = var.input_charge_type_cid == \"prePaid\" ? true : null\n}\n\nresource \"huaweicloud_compute_eip_associate\" \"associated\" {\n	public_ip   = huaweicloud_vpc_eip.eip.address\n	instance_id = huaweicloud_compute_instance.instance.id\n}\n');
INSERT INTO `terraform_template_data` VALUES (10, 'main', 2, 'create_server', 'bindPublicIp:false', 'terraform {\n	required_providers {\n		huaweicloud = {\n			source = \"huaweicloud/huaweicloud\"\n			version = \"1.44.2\"\n		}\n	}\n}\n\nprovider \"huaweicloud\" {\n	region=var.input_region_cid\n	access_key=var.input_secret_ak\n	secret_key=var.input_secret_sk\n}\n\nresource \"huaweicloud_compute_instance\" \"instance\" {\n	region                      = var.input_region_cid\n	name                        = var.input_name\n	image_id                    = var.input_server_image_cid\n	security_group_ids          = [var.input_security_group_cid]\n	availability_zone           = var.input_zone_cid\n	#admin_pass                  = var.remainder_loginPassword\n	key_pair                = var.input_key_pair_name\n	user_id    = \"fde318a43e6b4bbca7b201782dea8515\"\n	flavor_id                   = var.input_server_spec_cid\n	data_disks {\n		type = var.input_disk_type_cid\n		size = var.input_disk_size\n	}\n	// provider默认不能使用变量作为key，这里写死key值\n	tags = {\n		project = var.input_cloud_project_name\n	}\n	enterprise_project_id       = var.input_cloud_project_cid\n	delete_disks_on_termination = true\n	charging_mode               = var.input_charge_type_cid\n	period_unit                 = var.input_charge_type_cid == \"prePaid\" ? \"month\" : null\n	period                      = var.input_charge_type_cid == \"prePaid\" ? 1 : null\n	auto_renew                  = var.input_charge_type_cid == \"prePaid\" ? true : false\n	agent_list  = \"ces\"\n	network {\n		uuid = var.input_subnet_cid\n	}\n}');
INSERT INTO `terraform_template_data` VALUES (11, 'output', 2, 'create_server', 'bindPublicIp:false', 'output \"resource_id\" {\n  value = huaweicloud_compute_instance.instance.id\n}\noutput \"resource_name\" {\n  value = huaweicloud_compute_instance.instance.name\n}\noutput \"resource_ip_addr\" {\n  value = huaweicloud_compute_instance.instance.access_ip_v4\n}');
INSERT INTO `terraform_template_data` VALUES (12, 'output', 2, 'create_server', 'bindPublicIp:true', 'output \"resource_id\" {\n  value = huaweicloud_compute_instance.instance.id\n}\noutput \"resource_name\" {\n  value = huaweicloud_compute_instance.instance.name\n}\noutput \"resource_ip_addr\" {\n  value = huaweicloud_compute_instance.instance.access_ip_v4\n}\noutput \"public_resource_ip_addr\" {\n  value = huaweicloud_vpc_eip.eip.address\n}');
INSERT INTO `terraform_template_data` VALUES (13, 'main', 5, 'create_server', 'bindPublicIp:false,bindPublicIp:true', 'terraform {\n  required_providers {\n    aws = {\n      source  = \"hashicorp/aws\"\n      version = \"4.65.0\"\n    }\n  }\n}\n\nprovider \"aws\" {\n  access_key = var.input_secret_ak\n  secret_key = var.input_secret_sk\n  region     = var.input_region_cid\n}\ndata \"aws_ami\" \"image\" {\n  filter {\n    name   = \"name\"\n    values = [var.input_server_image_name]\n  }\n}\nresource \"aws_instance\" \"instance\" {\n  availability_zone           = var.input_zone_name\n  ami                         = data.aws_ami.image.id\n  associate_public_ip_address = var.input_bind_public_ip == \"true\" ? \"true\" : null\n  tags = {\n    project = var.input_cloud_project_name\n    Name    = var.input_name\n  }\n  instance_type          = var.input_server_spec_cid\n  key_name               = var.input_key_pair_name\n  vpc_security_group_ids = [var.input_security_group_cid]\n  subnet_id              = var.input_subnet_cid\n  #vpc_id                                  = var.input_vpc_cid\n  root_block_device {\n    volume_size = var.input_disk_size\n    volume_type = var.input_disk_type_cid\n  }\n  ebs_block_device {\n    volume_size = var.input_disk_size\n    volume_type = var.input_disk_type_cid\n    device_name = \"/dev/sdb\"\n  }\n  volume_tags = {\n    project = var.input_cloud_project_name\n    Name    = var.input_name\n  }\n}\nresource \"aws_eip\" \"instance\" {\n  count    = var.input_bind_public_ip == \"true\" ? 1 : 0\n  instance = aws_instance.instance.id\n  tags = {\n    project = var.input_cloud_project_name\n    Name    = var.input_name\n  }\n}');
